import jsonfrom dataclasses import dataclassimport pandas as pddef save_data(data, file_name="bd.json"):    with open(file_name, "w+", encoding="UTF-8") as f:        json.dump(data, f, indent=4)def load_data(file_name="bd.json"):    with open(file_name, "r+", encoding="UTF-8") as f:        data = json.load(f)    return data@dataclassclass Filled_link:    main_link: str    visitors_link: tuple@dataclassclass Process_link:    visitors_link: list    number_ship: int    number_of_past: intclass Work_links:    def __init__(self):        self.data: {str, Process_link} = {}        self.ready_link: list[Filled_link] = []    def __add_main_link(self, main_link: str):        self.data[main_link] = Process_link(visitors_link=[],                                            number_ship=0,                                            number_of_past=0)        return self    def __add__(self, other: str):        return self.__add_main_link(main_link=other)    def __iadd__(self, other: str):        return self.__add_main_link(main_link=other)    def ship_link(self, count: int):        for main_link in self.data:            if self.data[main_link].number_ship < count:                self.data[main_link].number_ship += 1                return main_link    def reception_link(self, main_link: str, visit_link: str, count: int):        self.data[main_link].visitors_link.append(visit_link)        self.data[main_link].number_of_past += 1        self.__add_main_link(main_link=visit_link)        if self.data[main_link].number_of_past == count:            links = tuple(self.data[main_link].visitors_link)            self.ready_link.append(Filled_link(main_link=main_link, visitors_link=links))            del self.data[main_link]    def del_link(self, main_link: str):        self.data[main_link].number_ship -= 1    def class_to_table(self):        file = {}        for fil_link in self.ready_link:            file[fil_link.main_link] = [*fil_link.visitors_link]        df = pd.DataFrame(file)        df.to_excel("table.xlsx", index=False)