from dataclasses import dataclassimport datetime@dataclassclass User:    username: str | None    link: str | None    flag: int | None    time_start: datetime    status_registration: bool | None    message_id: int | Noneclass nsql_database:    def __init__(self) -> None:        self.data = {}    # Получение значения по ключу    def get_elem(self, key: int | str) -> User | bool:        if key in self.data:            return self.data[key]        else:            return Falseclass Users(nsql_database):    def __init__(self):        super().__init__()    def __add_user(self, id):        self.data[id] = User(flag=0,                             message_id=0,                             username=None,                             link=None,                             time_start=datetime.datetime.now(),                             status_registration=None)        return self    def __add__(self, id: int):        return self.__add_user(id)    def __iadd__(self, id: int):        return self.__add_user(id)    def __contains__(self, other) -> bool:        if other in self.data:            return True        else:            return False